import * as fs from 'fs';
import * as path from 'path';

/**
 * Registers Redoc UI at /docs and serves the OpenAPI spec at /docs/openapi.yaml
 * using the provided Express server instance.
 */
export function registerDocsRoutes(server: import('express').Express) {
  const specPath = path.join(process.cwd(), 'docs', 'contracts', 'openapi.yaml');

  server.get('/docs/openapi.yaml', (_req, res) => {
    res.type('application/yaml');
    res.send(fs.readFileSync(specPath, 'utf8'));
  });

  server.get('/docs', (_req, res) => {
    res.type('html');
    res.send(`<!DOCTYPE html>
<html>
  <head>
    <title>Chat Backend API Docs</title>
    <meta charset="utf-8"/>
    <meta name="viewport" content="width=device-width, initial-scale=1">
    <script src="https://cdn.redoc.ly/redoc/latest/bundles/redoc.standalone.js"></script>
  </head>
  <body>
    <redoc spec-url="/docs/openapi.yaml"></redoc>
  </body>
</html>`);
  });
}

/**
 * Chat Backend Service - Bootstrap
 *
 * TRUST MODEL: This service is identity-agnostic and does NOT implement authentication.
 *
 * - userId is treated as untrusted, optional metadata for message attribution only
 * - NO identity verification or authorization is performed
 * - Upstream services MUST handle authentication before calling this backend
 * - Security relies on: IP-based rate limiting, input validation, network segmentation
 *
 * Production Deployment: Do NOT expose this service directly to public clients.
 * Always route through an authenticated upstream gateway or API service.
 *
 * Policy: docs/scope/no-auth-policy.md
 * Integration Guide: docs/scope/upstream-integration.md
 */

import { NestFactory } from "@nestjs/core";
import { AppModule } from "./app.module";
import { ValidationPipe } from "@nestjs/common";
import * as fs from "fs";
import * as path from "path";
import { ExpressAdapter } from "@nestjs/platform-express";
import express from "express";
import { registerDocsRoutes } from "./docs/registerDocs";
import {
  requestIdMiddleware,
  requestLoggingMiddleware,
  metricsMiddleware,
  metricsEndpoint,
} from "./middleware/logging";

async function bootstrap() {
  const server = express();
  const app = await NestFactory.create(AppModule, new ExpressAdapter(server));

  // Request ID generation (must be first)
  app.use(requestIdMiddleware);

  // Request logging with safe redaction
  app.use(requestLoggingMiddleware);

  // Metrics collection
  app.use(metricsMiddleware);

  // Global validation pipe
  app.useGlobalPipes(
    new ValidationPipe({
      whitelist: true,
      forbidNonWhitelisted: true,
      transform: true,
    }),
  );

  // Production-grade CORS restrictions
  app.enableCors({
    origin: process.env.CORS_ORIGIN?.split(",") || ["http://localhost:3000"],
    credentials: true,
    methods: ["GET", "POST", "PUT", "DELETE", "PATCH", "OPTIONS"],
    allowedHeaders: ["Content-Type", "Authorization", "X-Request-ID"],
    exposedHeaders: [
      "X-Request-ID",
      "RateLimit-Limit",
      "RateLimit-Remaining",
      "RateLimit-Reset",
    ],
    maxAge: 3600,
  });

  // Security headers (production-grade)
  app.use((req, res, next) => {
    res.setHeader(
      "Strict-Transport-Security",
      "max-age=31536000; includeSubDomains; preload",
    );
    res.setHeader("X-Content-Type-Options", "nosniff");
    res.setHeader("X-Frame-Options", "DENY");
    res.setHeader("X-XSS-Protection", "1; mode=block");
    res.setHeader("Referrer-Policy", "strict-origin-when-cross-origin");
    res.setHeader(
      "Permissions-Policy",
      "geolocation=(), microphone=(), camera=()",
    );
    next();
  });

  // Serve OpenAPI and Redoc at /docs
  registerDocsRoutes(server);

  // Global prefix for API routes
  app.setGlobalPrefix("api/v1");

  const port = process.env.PORT || 3001;

  // HTTPS configuration for production
  if (
    process.env.NODE_ENV === "production" &&
    process.env.SSL_KEY_PATH &&
    process.env.SSL_CERT_PATH
  ) {
    const httpsOptions = {
      key: fs.readFileSync(process.env.SSL_KEY_PATH),
      cert: fs.readFileSync(process.env.SSL_CERT_PATH),
    };

    // Note: For HTTPS, you would need to use NestFactory.create(AppModule, { httpsOptions })
    console.log(`Application is running on https://localhost:${port}`);
  } else {
    console.log(`Application is running on http://localhost:${port}`);
    console.log("");
    console.log("üîç Monitoring Endpoints:");
    console.log(`  - Health: http://localhost:${port}/health`);
    console.log(`  - Metrics: http://localhost:${port}/metrics`);
    console.log("");
    console.log("üìö Available API Endpoints:");
    console.log(`  - Chat: http://localhost:${port}/api/v1/chat`);
    console.log(`  - Users: http://localhost:${port}/api/v1/users`);
    console.log("");
    console.log("üõ°Ô∏è  Security: Rate limiting, validation, logging enabled");
    console.log("üìñ API Docs UI: http://localhost:" + port + "/docs");
    console.log("üìÑ OpenAPI Spec: http://localhost:" + port + "/docs/openapi.yaml");
  }

  await app.listen(port);
}

bootstrap().catch((error) => {
  console.error("Failed to start application:", error);
  process.exit(1);
});

import {
  Injectable,
  NotFoundException,
  BadRequestException,
} from "@nestjs/common";
import {
  UserRepository,
  UpdateUserDto,
  UserResponseDto,
} from "../data/user.repository";

@Injectable()
export class UserService {
  constructor(private readonly userRepository: UserRepository) {}

  async getUserById(id: string): Promise<UserResponseDto | null> {
    const user = await this.userRepository.findById(id);
    if (!user) {
      throw new NotFoundException("User not found");
    }

    return this.mapToResponseDto(user);
  }

  async getUserByEmail(email: string): Promise<UserResponseDto | null> {
    const user = await this.userRepository.findByEmail(email);
    return user ? this.mapToResponseDto(user) : null;
  }

  async getUserByUsername(username: string): Promise<UserResponseDto | null> {
    const user = await this.userRepository.findByUsername(username);
    return user ? this.mapToResponseDto(user) : null;
  }

  async updateUser(
    id: string,
    updateUserDto: UpdateUserDto,
  ): Promise<UserResponseDto> {
    const existingUser = await this.userRepository.findById(id);
    if (!existingUser) {
      throw new NotFoundException("User not found");
    }

    const updatedUser = await this.userRepository.update(id, updateUserDto);
    return this.mapToResponseDto(updatedUser);
  }

  async checkAnonymousPostLimit(userId: string): Promise<boolean> {
    const user = await this.userRepository.findById(userId);
    if (!user) {
      throw new NotFoundException("User not found");
    }

    // Check if it's a new day and reset count if needed
    const today = new Date();
    const lastPostDate = user.lastAnonymousPostDate;

    if (!lastPostDate || this.isDifferentDay(today, lastPostDate)) {
      await this.userRepository.resetAnonymousPostCount(userId);
      return true; // Can post
    }

    return user.anonymousPostsToday < 2;
  }

  async incrementAnonymousPostCount(userId: string): Promise<void> {
    await this.userRepository.incrementAnonymousPostCount(userId);
  }

  async checkWeeklyPushLimit(userId: string): Promise<boolean> {
    const user = await this.userRepository.findById(userId);
    if (!user) {
      throw new NotFoundException("User not found");
    }

    // Check if it's a new week and reset count if needed
    const today = new Date();
    const lastResetDate = user.lastWeeklyReset;

    if (this.isNewWeek(today, lastResetDate)) {
      await this.userRepository.resetWeeklyPushCount(userId);
      return true; // Can push
    }

    return user.weeklyPushesUsed < 5; // Assuming 5 pushes per week limit
  }

  async incrementWeeklyPushCount(userId: string): Promise<void> {
    await this.userRepository.incrementWeeklyPushCount(userId);
  }

  private mapToResponseDto(user: any): UserResponseDto {
    return {
      id: user.id,
      email: user.email,
      username: user.username,
      firstName: user.firstName,
      lastName: user.lastName,
      bio: user.bio,
      profileImageUrl: user.profileImageUrl,
      profileVisibility: user.profileVisibility,
      createdAt: user.createdAt,
      stats: {
        anonymousPostsToday: user.anonymousPostsToday || 0,
        weeklyPushesUsed: user.weeklyPushesUsed || 0,
      },
    };
  }

  private isDifferentDay(date1: Date, date2: Date): boolean {
    return date1.toDateString() !== date2.toDateString();
  }

  private isNewWeek(currentDate: Date, lastResetDate: Date): boolean {
    const weekInMs = 7 * 24 * 60 * 60 * 1000;
    return currentDate.getTime() - lastResetDate.getTime() > weekInMs;
  }

}
